mapper.insert:
INSERT IGNORE INTO guacamole_user_permission ( user_id, permission, affected_user_id ) SELECT permissions.user_id, permissions.permission, guacamole_user.user_id FROM
SELECT #{permission.userID,jdbcType=INTEGER} AS user_id, #{permission.type,jdbcType=VARCHAR} AS permission, #{permission.objectIdentifier,jdbcType=VARCHAR} AS username
AS permissions JOIN guacamole_user ON guacamole_user.username = permissions.username;
